/**
 * renders form to sign up w/ new account
 * renders "Sign Up" button that sends a POST request to API's /api/users 
 * and signs in user
 * renders "Cancel" button that returns user to list of courses
 */

import { useRef, useContext } from "react";

const UserSignUp = () => {

    // State
    const firstName = useRef(null);
    const lastName = useRef(null);
    const emailAddress = useRef(null);
    const password = useRef(null);

    // event handlers
    const handleSubmit = async (event) => {
        event.preventDefault();

        const user = {
            firstName: firstName.current.value,
            lastName: firstName.current.value,
            emailAddress: firstName.current.value,
            password: firstName.current.value,
        }


        return (
            <div className="form--centered">
                <h2>Sign Up</h2>

                <form onSubmit={handleSubmit}>
                    <label for="firstName">First Name</label>
                    <input
                        id="firstName"
                        name="firstName"
                        type="text"
                        ref={firstName} />
                    <label for="lastName">Last Name</label>
                    <input
                        id="lastName"
                        name="lastName"
                        type="text"
                        ref={lastName} />
                    <label for="emailAddress">Email Address</label>
                    <input
                        id="emailAddress"
                        name="emailAddress"
                        type="email"
                        ref={emailAddress} />
                    <label for="password">Password</label>
                    <input
                        id="password"
                        name="password"
                        type="password"
                        ref={password} />
                    <button className="button" type="submit">Sign Up</button>
                    <button className="button button-secondary"
                        onClick={(e) => {
                            e.preventDefault();
                            window.location.href = '/';
                        }}
                    >Cancel</button>
                </form>
                <p>Already have a user account? Click here to <Link to="/signin">sign in</Link>!</p>
            </div>
        )
    };
}
    export default UserSignUp;
